CREATE TABLE k_Standard (
    IDSTD integer,
    lodSTD character varying(255) NOT NULL,
    STD character varying(255),
    labelStandard character varying(255),
    descStandard text,
    lodTST character varying(255) NOT NULL,
    lodSTD_V character varying(255),
    startDate timestamp with time zone,
    populatedBy character varying(255),
    lastTouch character varying(255),
    PRIMARY KEY (lodSTD),
    FOREIGN KEY (lodTST) REFERENCES k_Type_Standard(lodTST) ON DELETE CASCADE
);


