CREATE TABLE k_Scheme (
    IDSCH integer,
    lodSCH character varying(255) NOT NULL,
    SCH character varying(255),
    labelScheme character varying(255),
    descScheme text,
    lodTSC character varying(255) NOT NULL,
    lodSCH_V character varying(255),
    startDate timestamp with time zone,
    populatedBy character varying(255),
    lastTouch character varying(255),
    PRIMARY KEY (lodSCH),
    FOREIGN KEY (lodTSC) REFERENCES k_Type_Scheme(lodTSC) ON DELETE CASCADE
);



