CREATE TABLE k_Resolver (
    lodRES character varying(255) NOT NULL,
    labelResolver character varying(255),
    descResolver text,
    defaultValue character varying(255),
    apiUrl character varying(255),
    httpMethod character varying(255),
    regexPattern character varying(255),
    primaryResolver character varying(255),
    curieFormat character varying(255),
    lastTouch character varying(255),
    populatedBy character varying(255),
    PRIMARY KEY (lodRES)
);

