CREATE TABLE k_Property (
    IDTPR integer,
    lodPRP character varying(255) NOT NULL,
    labelProperty character varying(255),
    descProperty text,
    lodCanonical character varying(255),
    alternateIdentifier text,
    IDPRP_P character varying(255),
    IDPRP_V character varying(255),
    populatedBy character varying(255),
    lastTouch character varying(255),
    PRIMARY KEY (lodPRP)
);


