INSERT INTO k_Provider (IDPRV, lodPRV, PRV, labelProvider, descProvider, lodTPR, lodPRV_V, Country, StartDate, populatedBy, lastTouch) VALUES (NULL, 'pid_graph:C794C66B', NULL, 'Chinese DOI', 'Chinese DOI is a DOI registration service jointly run by ISTIC & Wanfang Data, and it was the first DOI RA in China and Asia. It mainly provides DOI registration, resolution and other value-added services to Chinese publishers, research institutions, service providers and researchers for research content such as academic journals, scientific data, dissertations, books, conference proceedings, preprints, etc. ISTIC (Institute of Science and Technology Information of China) is a national public beneficial research institute under the Ministry of Science and Technology of China, and Wanfang Data (Wanfang Data Co., Ltd. & Wanfang Data CORP. (INTL) LTD) is the leading science and technology information service provider in China.', 'pid_graph:DB893DE8', NULL, 'CHN', NULL, '0009-0006-1756-5317', '2024-08-20');
