INSERT INTO k_Provider (IDPRV, lodPRV, PRV, labelProvider, descProvider, lodTPR, lodPRV_V, Country, StartDate, populatedBy, lastTouch) VALUES (NULL, 'pid_graph:33A4BB68', NULL, 'KISTI', 'The Korean DOI Center is operated by the Korea Institute of Science and Technology Information (KISTI). They collect bibliographic information of Korean science and technology information and data, including journal articles, proceedings, official Government documents, Korean patent information, R&D Reports and traditional Korean knowledge. In addition, the Korean DOI Center promotes the use of the collected information and data in Korea and improve access to Korean research achievements.', 'pid_graph:DB893DE8', NULL, 'KOR', NULL, '0009-0006-1756-5317', '2024-08-20');
