INSERT INTO k_Provider (IDPRV, lodPRV, PRV, labelProvider, descProvider, lodTPR, lodPRV_V, Country, StartDate, populatedBy, lastTouch) VALUES (NULL, 'pid_graph:D2D0AB0F', NULL, 'Bibliothèque nationale de Luxembourg', 'The Bibliothéque Nationale de Luxembourg collects, catalogues and preserves all analogue and digital publications published in Luxembourg and any works published abroad that relate to the Grand Duchy. It is the country’s leading academic, research and cultural heritage library. In order to meet the requirements of its users, about three-quarters of its printed collections come from other countries and they cover different fields of knowledge.', 'pid_graph:DB893DE8', NULL, 'LUX', NULL, '0009-0006-1756-5317', '2024-08-20');
