INSERT INTO k_Provider (IDPRV, lodPRV, PRV, labelProvider, descProvider, lodTPR, lodPRV_V, Country, StartDate, populatedBy, lastTouch) VALUES (NULL, 'pid_graph:66DB4934', NULL, 'Bodleian Library, University of Oxford', 'The Bodleian Libraries is the library service supporting the University of Oxford. The mission of the Bodleian Libraries is to provide an excellent service to support the learning, teaching and research objectives of the University of Oxford; and to develop and maintain access to Oxford''s unique collections for the benefit of scholarship and society.', 'pid_graph:DB893DE8', NULL, 'GBR', NULL, '0000-0002-0255-5101', '2024-08-19');
INSERT INTO k_Provider (IDPRV, lodPRV, PRV, labelProvider, descProvider, lodTPR, lodPRV_V, Country, StartDate, populatedBy, lastTouch) VALUES (NULL, 'pid_graph:692E6BE7', NULL, 'British Library', 'The British Library is the national library of the UK. The library is free to use and open to everyone. It works with partners and other libraries to make its collection as accessible as possible.', 'pid_graph:DB893DE8', NULL, 'GBR', NULL, '0009-0006-1756-5317', '2024-08-20');
INSERT INTO k_Provider (IDPRV, lodPRV, PRV, labelProvider, descProvider, lodTPR, lodPRV_V, Country, StartDate, populatedBy, lastTouch) VALUES (NULL, 'pid_graph:8C8A02AB', NULL, 'Cambridge University Library', 'The library of Cambridge University preserves the worlds knowledge in physical and digital form. As one of the UK and Ireland’s six Legal Deposit Libraries, Cambridge University Library has been entitled to request every publication in both the UK and Ireland since 1710. In addition to its physical collections, the library allows access to tens of millions of electronic articles, books, journals, scores and other e-resources.', 'pid_graph:DB893DE8', NULL, 'GBR', NULL, '0009-0006-1756-5317', '2024-08-20');
INSERT INTO k_Provider (IDPRV, lodPRV, PRV, labelProvider, descProvider, lodTPR, lodPRV_V, Country, StartDate, populatedBy, lastTouch) VALUES (NULL, 'pid_graph:6ADA1073', NULL, 'National Library of Scotland', 'The National Library of Scotland is answerable to the Scottish Parliament and funded by the Scottish Government. It remains one of only six legal deposit libraries in the United Kingdom and Ireland and is governed by a board of management. Its mission is to enhance Scotland''s international reputation by making a significant contribution to global knowledge and the memory of the world.', 'pid_graph:DB893DE8', NULL, 'GBR', NULL, '0000-0002-0255-5101', '2024-08-19');
INSERT INTO k_Provider (IDPRV, lodPRV, PRV, labelProvider, descProvider, lodTPR, lodPRV_V, Country, StartDate, populatedBy, lastTouch) VALUES (NULL, 'pid_graph:591D90E5', NULL, 'National Library of Wales', 'The National Library of Wales (NLW) is a legal deposit library holding a right to a copy of every publication printed in Britain and Ireland. The library is physically located in Abserystwyth but some of its collection has been digitized and can be viewed online.   ', 'pid_graph:DB893DE8', NULL, 'GBR', NULL, '0009-0006-1756-5317', '2024-08-20');
INSERT INTO k_Provider (IDPRV, lodPRV, PRV, labelProvider, descProvider, lodTPR, lodPRV_V, Country, StartDate, populatedBy, lastTouch) VALUES (NULL, 'pid_graph:4CA9BB01', NULL, 'UK National Archives', NULL, 'pid_graph:DB893DE8', NULL, 'GBR', NULL, '0009-0006-1756-5317', '2025-01-19');
INSERT INTO k_Provider (IDPRV, lodPRV, PRV, labelProvider, descProvider, lodTPR, lodPRV_V, Country, StartDate, populatedBy, lastTouch) VALUES (NULL, 'pid_graph:A7074BF7', NULL, 'AETIS', 'AETIS is a digital platform designed to support the growth and development of businesses and individuals working in creative industries, such as music, art, design, film, and advertising, by providing them with online tools, and resources, to protect their Intellectual Property Rights almost instantly. AETIS brings together innovative technology and IP protection experts worldwide to lay the foundation for the global expansion of creative industries, culture and innovative businesses.', 'pid_graph:F1662576', NULL, NULL, NULL, '0009-0006-1756-5317', '2024-08-20');
INSERT INTO k_Provider (IDPRV, lodPRV, PRV, labelProvider, descProvider, lodTPR, lodPRV_V, Country, StartDate, populatedBy, lastTouch) VALUES (NULL, 'pid_graph:4491BAB1', NULL, 'ARK Alliance', 'The ARK Alliance is an open global community supporting the ARK infrastructure on behalf of research and scholarship. Archival Resource Keys (ARKs) serve as persistent identifiers, or stable, trusted references for information objects. Among other things, they aim to be web addresses (URLs) that don’t return 404 Page Not Found errors.', 'pid_graph:FBFDB448', NULL, NULL, NULL, '0009-0006-1756-5317', '2024-08-20');
