INSERT INTO k_Provider (IDPRV, lodPRV, PRV, labelProvider, descProvider, lodTPR, lodPRV_V, Country, StartDate, populatedBy, lastTouch) VALUES (NULL, 'pid_graph:01F7DFBA', NULL, 'BSI Identify', 'BSI Identify is a service operated by BSI to provide unambiguous identification for products and the long-term persistent storage of associated product information that can be accessed using a digital ID called a UPIN (Unique Persistent Identification Number). The service provides a consistent approach to identification of construction products that supports the industry as it continues to digitize and addresses critical safety issues.', 'pid_graph:F1662576', NULL, NULL, NULL, '0009-0006-1756-5317', '2024-08-20');
INSERT INTO k_Provider (IDPRV, lodPRV, PRV, labelProvider, descProvider, lodTPR, lodPRV_V, Country, StartDate, populatedBy, lastTouch) VALUES (NULL, 'pid_graph:C9E0E092', NULL, 'BTLF (Société de gestion de la Banque de Titres de Langue Française)', 'The BLTF management company contributes to improving the marketing of French-language publishing products in Canada and Quebec; fostering the promotion and encouraging the marketing of Canadian and Quebec French-language publishing products abroad; encouraging and fostering communication and collaboration between publishers, booksellers, distributors, librarians and other participants in the book industry.', 'pid_graph:F1662576', NULL, NULL, NULL, '0009-0006-1756-5317', '2024-08-20');
INSERT INTO k_Provider (IDPRV, lodPRV, PRV, labelProvider, descProvider, lodTPR, lodPRV_V, Country, StartDate, populatedBy, lastTouch) VALUES (NULL, 'pid_graph:33C4D427', NULL, 'Casalini Libri', 'Casalini Libri supplies publications from across Southern Europe to libraries and institutions worldwide. It provides services for libraries and publishers. Its Torrossa platforms aims to simplify acquisitions, licensing, and integration with the library catalogue.', 'pid_graph:F1662576', NULL, NULL, NULL, '0009-0006-1756-5317', '2024-08-20');
