INSERT INTO k_Provider (IDPRV, lodPRV, PRV, labelProvider, descProvider, lodTPR, lodPRV_V, Country, StartDate, populatedBy, lastTouch) VALUES (NULL, 'pid_graph:158F1A3C', NULL, 'CLARIN', 'The Common Language Resources and Technology Infrastructure (CLARIN) is a digital infrastructure which provides easy and sustainable access to a broad range of language data and tools to support research in the humanities and social sciences, and beyond. CLARIN provides access to multimodal digital language data (text, audio, video) and advanced tools with which to explore, analyse or combine these datasets.', 'pid_graph:FBFDB448', NULL, NULL, NULL, '0009-0006-1756-5317', '2024-08-20');
