INSERT INTO k_Provider (IDPRV, lodPRV, PRV, labelProvider, descProvider, lodTPR, lodPRV_V, Country, StartDate, populatedBy, lastTouch) VALUES (NULL, 'pid_graph:D51999D1', NULL, 'CrossRef', 'Crossref is a global community infrastructure making all kinds of research objects easy to find, assess, and reuse through a number of services critical to research communications. Their 18,000+ members come from 148 countries and their ~140 million DOI records contribute to the collective vision of a rich and reusable open network of relationships connecting research organisations, people, things, and actions; a scholarly record that the global community can build on forever, for the benefit of society.', 'pid_graph:FBFDB448', NULL, NULL, NULL, '0000-0002-0255-5101', '2024-08-19');
INSERT INTO k_Provider (IDPRV, lodPRV, PRV, labelProvider, descProvider, lodTPR, lodPRV_V, Country, StartDate, populatedBy, lastTouch) VALUES (NULL, 'pid_graph:258448F0', NULL, 'DataCite', 'DataCite is a global non-profit organisation that provides DOIs for research data and all other research outputs. By assigning DataCite DOIs, research outputs become discoverable and associated metadata is made available to the community. DataCite develops additional services to make it easy to connect and share research outputs with the broader research ecosystem and to assess the use of outputs within that ecosystem. All organisations within the research community can join DataCite to start registering DOIs.', 'pid_graph:FBFDB448', NULL, NULL, NULL, '0000-0002-0255-5101', '2024-08-19');
