INSERT INTO k_Provider (IDPRV, lodPRV, PRV, labelProvider, descProvider, lodTPR, lodPRV_V, Country, StartDate, populatedBy, lastTouch) VALUES (NULL, 'pid_graph:23DB5BB4', NULL, 'EIDR', 'The Entertainment Identifier Registry (EIDR) provides identification services for Content Identifers and Video Service Identifers. Content IDs are a registry of movies, television shows, radio programs, podcasts, and other audio/video assets of commercial, cultural, historic, or scientific interest. Video Service IDs are a registry of content delivery services including terrestrial broadcast channels, satellite delivery channels, cable television networks, Internet streaming services, etc. EIDR IDs are commonly used for process automation, linked data, engineering standards, academic citation, universal search, and so forth.', 'pid_graph:F1662576', NULL, NULL, NULL, '0009-0006-1756-5317', '2024-08-20');
