INSERT INTO k_Provider (IDPRV, lodPRV, PRV, labelProvider, descProvider, lodTPR, lodPRV_V, Country, StartDate, populatedBy, lastTouch) VALUES (NULL, 'pid_graph:8B3C5C36', NULL, 'HAND', 'Human & Digital (HAND) is a Talent Identifier built for the business requirements of Hollywood & sports. By identifying Legal Entity Humans, Licensed Virtual Humans, and Fictional Characters, HAND provides the interoperable Talent ID the industry needs for historical, present, and future uses.', 'pid_graph:F1662576', NULL, NULL, NULL, '0009-0006-1756-5317', '2024-08-20');
INSERT INTO k_Provider (IDPRV, lodPRV, PRV, labelProvider, descProvider, lodTPR, lodPRV_V, Country, StartDate, populatedBy, lastTouch) VALUES (NULL, 'pid_graph:DCD3FC34', NULL, 'IANA', 'The Internet Assigned Numbers Authority (IANA) is a standards organisation that oversees global IP address allocation, autonomous system number allocation, root zone management in the Domain Name System (DNS), media types, and other Internet Protocol–related symbols and Internet numbers.', 'pid_graph:F1662576', NULL, NULL, NULL, '0009-0006-1756-5317', '2024-08-20');
INSERT INTO k_Provider (IDPRV, lodPRV, PRV, labelProvider, descProvider, lodTPR, lodPRV_V, Country, StartDate, populatedBy, lastTouch) VALUES (NULL, 'pid_graph:65D51A09', NULL, 'ICCU - Istituto Centrale per il Catalogo Unico della Biblioteche Italiane per le informazioni bibliografiche', 'The Central Institute for the Union Catalogue of Italian Libraries and Bibliographic Information (Istituto Centrale per il Catalogo Unico delle biblioteche italiane e per le informazioni bibliografiche - ICCU) was created in 1951 with the task of cataloguing the entire national bibliographic heritage. The Institute cooperates with the Italian libraries carrying out coordination functions and promoting cataloguing projects which make the use of information technology.', 'pid_graph:F1662576', NULL, NULL, NULL, '0009-0006-1756-5317', '2024-08-20');
