INSERT INTO k_Provider (IDPRV, lodPRV, PRV, labelProvider, descProvider, lodTPR, lodPRV_V, Country, StartDate, populatedBy, lastTouch) VALUES (NULL, 'pid_graph:6498B100', NULL, 'IGSN e.v.', 'The IGSN ID is a globally unique, persistent, domain-agnostic identifier for material samples. Samples can be any material from anywhere in the universe, not only from Earth. Even though IGSN had its roots in geosciences, it really is now for all sample types from all disciplines.', 'pid_graph:F1662576', NULL, NULL, NULL, '0009-0006-1756-5317', '2024-08-20');
