INSERT INTO k_Identifier (IDIDN, lodIDN, IDN, labelIdentifier, descIdentifier, lodIND_V, STartDate, populatedBy, lastTouch) VALUES (NULL, 'pid_graph:075D8338', 'AID', 'AID', 'PubChem BioAssay contains small-molecule and RNAi screening data along with associated annotation information from contributing organizations. BioAssay contains a collection of bioactivity and toxicity data that has greatly supported research in fields such as medicinal chemistry, drug discovery, pharmaceutical genomics and informatics research. As a primary archive, the provenance of all records belongs to the submitter.', NULL, NULL, '0000-0002-0255-5101', '30/01/2024');
INSERT INTO k_Identifier (IDIDN, lodIDN, IDN, labelIdentifier, descIdentifier, lodIND_V, STartDate, populatedBy, lastTouch) VALUES (NULL, 'pid_graph:15BEDB40', 'ARK', 'ARK', 'Multi-purpose persistent identifier for information objects of any type. Archival Resource Keys (ARKs) serve as persistent identifiers, or stable, trusted references for information objects. Among other things, they aim to be web addresses (URLs) that don’t return 404 Page Not Found errors. The ARK Alliance is an open global community supporting the ARK infrastructure on behalf of research and scholarship. End users, especially researchers, rely on ARKs for long term access to the global scientific and cultural record. Since 2001 some 8.2 billion ARKs have been created by over 1000 organizations — libraries, data centers, archives, museums, publishers, government agencies, and vendors. They identify anything digital, physical, or abstract. ARKs are open, mainstream, non-paywalled, decentralized persistent identifiers that can be created by an organization as soon as it is registered with a NAAN (Name Assigning Authority Number). Once registered, an ARK organization can create unlimited numbers of ARKs and publicize them via the n2t.net global resolver or via their own local resolver.', NULL, NULL, '0000-0002-0255-5101', '30/01/2024');
INSERT INTO k_Identifier (IDIDN, lodIDN, IDN, labelIdentifier, descIdentifier, lodIND_V, STartDate, populatedBy, lastTouch) VALUES (NULL, 'pid_graph:03A715EA', 'arXiv', 'arXiv', 'Article identifier scheme used by arXiv. arXiv is an e-print service in the fields of physics, mathematics, non-linear science, computer science, and quantitative biology.', NULL, NULL, '0000-0002-0255-5101', '30/01/2024');
