INSERT INTO k_Identifier (IDIDN, lodIDN, IDN, labelIdentifier, descIdentifier, lodIND_V, STartDate, populatedBy, lastTouch) VALUES (NULL, 'pid_graph:D1BA9B24', 'dPID', 'dPID', 'DIDs  (dPIDs) are a new type of identifier that enables verifiable, self-sovereign digital identity using blockchain and DLT. DIDs are an effort by the W3C Credentials Community Group and the wider Internet identity community to define identifiers that can be registered, updated, resolved, and revoked without any dependency on a central authority or intermediary.', NULL, NULL, '0000-0002-0255-5101', '30/01/2024');
