INSERT INTO k_Identifier (IDIDN, lodIDN, IDN, labelIdentifier, descIdentifier, lodIND_V, STartDate, populatedBy, lastTouch) VALUES (NULL, 'pid_graph:7D9E4E44', 'Magnet', 'Magnet', 'Magnet is a URI scheme that defines the format of magnet links, a de facto standard for identifying files (URN) by their content, via cryptographic hash value rather than by their location.', NULL, NULL, '0000-0002-0255-5101', '30/01/2024');
INSERT INTO k_Identifier (IDIDN, lodIDN, IDN, labelIdentifier, descIdentifier, lodIND_V, STartDate, populatedBy, lastTouch) VALUES (NULL, 'pid_graph:B36773C9', 'MeSH ID        ', 'MeSH ID        ', 'MeSH (Medical Subject Headings) is the NLM controlled vocabulary thesaurus used for indexing articles for PubMed.', NULL, NULL, '0000-0002-0255-5101', '30/01/2024');
INSERT INTO k_Identifier (IDIDN, lodIDN, IDN, labelIdentifier, descIdentifier, lodIND_V, STartDate, populatedBy, lastTouch) VALUES (NULL, 'pid_graph:FE96C149', 'MMDB-ID', 'MMDB-ID', 'The Conserved Domain Database (CDD) brings together several collections of multiple sequence alignments representing conserved domains, in addition to NCBI-curated domains that use 3D-structure information explicitly to define domain boundaries and provide insights into sequence/structure/function relationships.', NULL, NULL, '0000-0002-0255-5101', '30/01/2024');
INSERT INTO k_Identifier (IDIDN, lodIDN, IDN, labelIdentifier, descIdentifier, lodIND_V, STartDate, populatedBy, lastTouch) VALUES (NULL, 'pid_graph:6AAA1ABD', 'RoR', 'RoR', 'ROR (Research Organization Registry) is a global, community-led registry of open persistent identifiers for research organizations. ROR is jointly operated by California Digital Library, Crossref, and Datacite.', NULL, NULL, '0000-0002-0255-5101', '30/01/2024');
INSERT INTO k_Identifier (IDIDN, lodIDN, IDN, labelIdentifier, descIdentifier, lodIND_V, STartDate, populatedBy, lastTouch) VALUES (NULL, 'pid_graph:50E7B5FE', 'NLM Catalog ID        ', 'NLM Catalog ID        ', 'The NLM Catalog provides access to NLM bibliographic data for journals, books, audiovisuals, computer software, electronic resources and other materials. Links to the library''s holdings in LocatorPlus, NLM''s online public access catalog, are also provided.', NULL, NULL, '0000-0002-0255-5101', '30/01/2024');
INSERT INTO k_Identifier (IDIDN, lodIDN, IDN, labelIdentifier, descIdentifier, lodIND_V, STartDate, populatedBy, lastTouch) VALUES (NULL, 'pid_graph:AF81E099', 'Nucleotide GI number', 'Nucleotide GI number', 'The Nucleotide database is a collection of sequences from several sources, including GenBank, RefSeq, TPA and PDB. Genome, gene and transcript sequence data provide the foundation for biomedical research and discovery.', NULL, NULL, '0000-0002-0255-5101', '30/01/2024');
INSERT INTO k_Identifier (IDIDN, lodIDN, IDN, labelIdentifier, descIdentifier, lodIND_V, STartDate, populatedBy, lastTouch) VALUES (NULL, 'pid_graph:F8110591', 'NZBN', 'NZBN', 'The NZBN (New Zealand Business Number) is a unique identifier assigned to businesses and other organisations in New Zealand. ', NULL, NULL, '0000-0002-0255-5101', '30/01/2024');
INSERT INTO k_Identifier (IDIDN, lodIDN, IDN, labelIdentifier, descIdentifier, lodIND_V, STartDate, populatedBy, lastTouch) VALUES (NULL, 'pid_graph:AB1807F6', 'OCLC Control Number', 'OCLC Control Number', 'OCLC assigns a unique number to each bibliographic record input into WorldCat.', NULL, NULL, '0000-0002-0255-5101', '30/01/2024');
INSERT INTO k_Identifier (IDIDN, lodIDN, IDN, labelIdentifier, descIdentifier, lodIND_V, STartDate, populatedBy, lastTouch) VALUES (NULL, 'pid_graph:08EE6DB9', 'OMID', 'OMID', 'Every entity in OpenCitations Meta (https://opencitations.net/meta) is assigned persistent internal identifier called OpenCitations Meta Identifier (OMID). The OMID has structure [[entity_type_abbreviation]]/[[supplier_prefix]][[sequential_number]]. For example, the first journal article ever processed has OMID br/0601 (the full URI is https://w3id.org/oc/meta/br/0601), where br is the abbreviation of bibliographic resource, and 060 corresponds to the supplier prefix, helpful in recognising at a glance the index it belongs to (i.e., OpenCitations Meta). Finally, 1 indicates that this is the index''s first bibliographic resource ever minted.', NULL, NULL, '0000-0002-0255-5101', '30/01/2024');
INSERT INTO k_Identifier (IDIDN, lodIDN, IDN, labelIdentifier, descIdentifier, lodIND_V, STartDate, populatedBy, lastTouch) VALUES (NULL, 'pid_graph:6AE88038', 'OpenCitations Corpus', 'OpenCitations Corpus', 'The OpenCitations Corpus is open repository of scholarly citation data made available under a Creative Commons public domain dedication (CC0), which provides accurate bibliographic references harvested from the scholarly literature that others may freely build upon, enhance and reuse for any purpose, without restriction under copyright or database law.', NULL, NULL, '0000-0002-0255-5101', '30/01/2024');
INSERT INTO k_Identifier (IDIDN, lodIDN, IDN, labelIdentifier, descIdentifier, lodIND_V, STartDate, populatedBy, lastTouch) VALUES (NULL, 'pid_graph:9C827E94', 'OpenURL', 'OpenURL', 'An OpenURL is similar to a web address, but instead of referring to a physical website, it refers to an article, book, patent, or other resource within a website', NULL, NULL, '0000-0002-0255-5101', '30/01/2024');
INSERT INTO k_Identifier (IDIDN, lodIDN, IDN, labelIdentifier, descIdentifier, lodIND_V, STartDate, populatedBy, lastTouch) VALUES (NULL, 'pid_graph:152D5274', 'ORCID', 'ORCID', 'ORCID (Open Researcher and Contributor ID) is an open, non-profit, community-based effort to create and maintain a registry of unique identifiers for individual researchers. ORCID records hold non-sensitive information such as name, email, organization name, and research activities.', NULL, NULL, '0000-0002-0255-5101', '30/01/2024');
INSERT INTO k_Identifier (IDIDN, lodIDN, IDN, labelIdentifier, descIdentifier, lodIND_V, STartDate, populatedBy, lastTouch) VALUES (NULL, 'pid_graph:152D52741', 'PIC', 'PIC', 'The PIC is a unique identifier for your organisation that is used to track and manage participants in EU-funded programs, projects, and initiatives', NULL, NULL, '0000-0002-0255-5101', '30/01/2024');
