INSERT INTO k_Identifier (IDIDN, lodIDN, IDN, labelIdentifier, descIdentifier, lodIND_V, STartDate, populatedBy, lastTouch) VALUES (NULL, 'pid_graph:08629E50', 'PMID', 'PMID', 'A PMID (PubMed identifier or PubMed unique identifier) is a unique integer value, starting at 1, assigned to each PubMed record.', NULL, NULL, '0000-0002-0255-5101', '30/01/2024');
INSERT INTO k_Identifier (IDIDN, lodIDN, IDN, labelIdentifier, descIdentifier, lodIND_V, STartDate, populatedBy, lastTouch) VALUES (NULL, 'pid_graph:AE530285', 'PopSet ID', 'PopSet ID', 'The PopSet database is a collection of related DNA sequences derived from population, phylogenetic, mutation and ecosystem studies that have been submitted to GenBank.', NULL, NULL, '0000-0002-0255-5101', '30/01/2024');
INSERT INTO k_Identifier (IDIDN, lodIDN, IDN, labelIdentifier, descIdentifier, lodIND_V, STartDate, populatedBy, lastTouch) VALUES (NULL, 'pid_graph:D0FEC2B9', 'Probe ID	', 'Probe ID	', 'The NCBI Probe Database is a public registry of nucleic acid reagents designed for use in a wide variety of biomedical research applications, together with information on reagent distributors, probe effectiveness, and computed sequence similarities.', NULL, NULL, '0000-0002-0255-5101', '30/01/2024');
INSERT INTO k_Identifier (IDIDN, lodIDN, IDN, labelIdentifier, descIdentifier, lodIND_V, STartDate, populatedBy, lastTouch) VALUES (NULL, 'pid_graph:A2F84B54', 'Protein Cluster ID', 'Protein Cluster ID', 'This collection of related protein sequences (clusters) consists of proteins derived from the annotations of whole genomes, organelles and plasmids. It currently limited to Archaea, Bacteria, Plants, Fungi, Protozoans, and Viruses', NULL, NULL, '0000-0002-0255-5101', '30/01/2024');
INSERT INTO k_Identifier (IDIDN, lodIDN, IDN, labelIdentifier, descIdentifier, lodIND_V, STartDate, populatedBy, lastTouch) VALUES (NULL, 'pid_graph:4A58E440', 'Protein GI number        ', 'Protein GI number        ', 'The Protein database is a collection of sequences from several sources, including translations from annotated coding regions in GenBank, RefSeq and TPA, as well as records from SwissProt, PIR, PRF, and PDB. Protein sequences are the fundamental determinants of biological structure and function.', NULL, NULL, '0000-0002-0255-5101', '30/01/2024');
